update.foreach:
update kz_sku
spu_id=#{sku.spuId}, title=#{sku.title}, images=#{sku.images}, new_price=#{sku.newPrice}, price=#{sku.price}, indexes=#{sku.indexes}, own_spec=#{sku.ownSpec}, enable=#{sku.enable}, last_update_time=NOW(), shop_id=#{sku.shopId}, update_name=#{sku.updateName}
where id = #{sku.id}

mapper.select:
SELECT * FROM KZ_SKU SKU WHERE SKU.`ENABLE` = '1'
AND ID IN

    #{item.skuId}
