mapper.update:
update kz_sku set is_features = #{isFeatures}
where id in

    #{item}

mapper.select:
SELECT feat.features_id,spu.title,sku.own_spec,feat.features_price,feat.features_stock,feat.features_status, CONCAT(date_format(feat.specialstart_time,'%m-%d %k:%i'),' - ',date_format(feat.specialend_time,'%m-%d %k:%i')) specialTime, feat.create_time,feat.create_name,feat.features_weight,feat.remarks,spu.image FROM kz_spu_features feat LEFT JOIN kz_spu spu ON feat.spu_id = spu.id LEFT JOIN kz_sku sku ON feat.sku_id = sku.id
feat.shop_id = #{shopId}
and spu.title like CONCAT('%',#{title},'%')
and feat.features_status = #{status}
order by feat.create_time desc
