mapper.select:
select spu.id,spu.title,sub_title,spu.saleable,CONCAT(cate1.`name`,'/',cate2.`name`,'/',cate3.`name`) as 'cName',br.`name` AS 'bName', spu.create_time,spu.last_update_time,spu.update_name,spu.images,spu.image from kz_spu spu LEFT JOIN kz_category cate1 ON spu.cid1 = cate1.id and cate1.is_flag='0' LEFT JOIN kz_category cate2 ON spu.cid2 = cate2.id and cate2.is_flag='0' LEFT JOIN kz_category cate3 ON spu.cid3 = cate3.id and cate3.is_flag='0' LEFT JOIN kz_brand br ON spu.brand_id = br.id and br.is_flag = '0' LEFT JOIN kz_shop shop ON spu.shop_id = shop_id
spu.is_flag = '0'
and shop_id = #{shopId}
and title like CONCAT('%',#{title},'%')
and saleable = #{saleable}
and cid3 = #{cid3}
and brand_id = #{brandId}
GROUP BY spu.id ORDER BY spu.last_update_time DESC,spu.create_time DESC

select spu.id,spu.title,sub_title,spu.saleable,cid1,cid2,cid3,spu.brand_id, spu.create_time,spu.last_update_time,spu.update_name,images,image from kz_spu spu LEFT JOIN kz_category cate1 ON spu.cid1 = cate1.id and cate1.is_flag='0' LEFT JOIN kz_category cate2 ON spu.cid2 = cate2.id and cate2.is_flag='0' LEFT JOIN kz_category cate3 ON spu.cid3 = cate3.id and cate3.is_flag='0' LEFT JOIN kz_brand br ON spu.brand_id = br.id and br.is_flag = '0' LEFT JOIN kz_shop shop ON spu.shop_id = shop_id
spu.is_flag = '0'
and shopId = #{shopId}
and title like CONCAT('%',#{title},'%')
and saleable = #{saleable}
and cid3 = #{cid3}
and brand_id = #{brandId}
ORDER BY spu.last_update_time DESC,spu.create_time DESC

select brand_id as 'key',`name` as 'brandName' from kz_brand br
LEFT JOIN kz_category_brand cb on br.id = cb.brand_id


        cb.category_id = #{categoryId}

mapper.update:
update kz_spu
title=#{title}, sub_title=#{subTitle}, cid1=#{cid1}, cid2=#{cid2}, cid3=#{cid3}, brand_id=#{brandId}, is_flag=#{isFlag}, last_update_time=NOW(), update_name=#{updateName}, images=#{images}, image=#{image}, shop_id=#{shopId},
where id=#{id}

mapper.select:
select spu.id as id,spu.title as title,spu.sub_title as subTitle,spu.image as image,min(sku.price) as price,stock
from kz_spu spu left join kz_sku sku on spu.id = sku.spu_id
LEFT JOIN kz_stock stock ON sku.id = stock.sku_id
where

    spu.cid2 = #{cateId}


    spu.cid3 = #{cateId}

AND spu.shop_id = #{shopId}
AND SPU.SALEABLE = '1'
AND spu.is_flag = '0'
group by spu.id

select id as 'spuId',title,sub_title,cid3,description,specifications,spec_template,packing_list,after_service,images,shop_id from kz_spu s
LEFT JOIN kz_spu_detail detail ON s.id = detail.spu_id
where s.id = #{spuId}

select id from kz_spu WHERE      shop_id = #{shopId}


        and  cid3 = #{ci3}
        and id <> #{spuId}


        and cid1 = #{ci3}


and is_flag = '0' and saleable = '1'

select sku.id as 'skuId',spu.id as 'spuId',spu.title as 'spuTitle',spu.image as 'image', sku.new_price as 'spuNewPrice',sku.price as 'spuPrice' from kz_spu spu LEFT JOIN kz_sku sku ON spu.id = sku.spu_id where spu.id in
#{item}
GROUP BY spu.id ORDER BY new_price
