mapper.select:
select wheel_id,wheel_images,wheel_no,wheel_url,wheel_name,wheel_isflag,shop.shop_name from kz_wheel wheel
LEFT JOIN kz_shop shop ON wheel.shop_id = shop.id


        and shop_id = #{shopId}


        and is_flag = #{isFlag}


        and wheel_port = #{wheelPort}


        and wheel_name like CONCAT('%',#{wheelName},'%')


order by wheel_no,wheel.update_time desc,wheel.create_time desc

select wheel_id,wheel_images,wheel_no,wheel_url,wheel_name,wheel_port,wheel_isflag,shop.shop_name,wheel.create_time,wheel.update_time,wheel.update_name,wheel.is_flag from kz_wheel wheel
LEFT JOIN kz_shop shop ON wheel.shop_id = shop.id


        and shop_id = #{shopId}


        and is_flag = #{isFlag}


        and wheel_port = #{wheelPort}


        and wheel_name like CONCAT('%',#{wheelName},'%')


        and wheel_isflag = #{wheelIsflag}


order by wheel_no,wheel.update_time desc,wheel.create_time desc
